import React, { useState } from 'react';
import api from './api'; // Adjust the path if needed

function CustomerForm() {
  const [form, setForm] = useState({
    first_name: '',
    last_name: '',
    phone: '',
    city: '',
    state: '',
    pincode: '',
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await api.post('/customers', form);
      alert('Customer created with ID: ' + response.data.id);
      setForm({
        first_name: '',
        last_name: '',
        phone: '',
        city: '',
        state: '',
        pincode: '',
      });
    } catch (error) {
      alert('Error creating customer: ' + (error.response?.data?.error || error.message));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="first_name" value={form.first_name} onChange={handleChange} placeholder="First Name" required />
      <input name="last_name" value={form.last_name} onChange={handleChange} placeholder="Last Name" required />
      <input name="phone" value={form.phone} onChange={handleChange} placeholder="Phone" required />
      <input name="city" value={form.city} onChange={handleChange} placeholder="City" />
      <input name="state" value={form.state} onChange={handleChange} placeholder="State" />
      <input name="pincode" value={form.pincode} onChange={handleChange} placeholder="Pincode" />
      <button type="submit">Add Customer</button>
    </form>
  );
}

export default CustomerForm;
