import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddressForm from './AddressForm';

function AddressList({ customerId }) {
  const [addresses, setAddresses] = useState([]);
  const [editingId, setEditingId] = useState(null);

  function fetchAddresses() {
    axios.get(`http://localhost:5000/api/customers/${customerId}/addresses`)
      .then(res => setAddresses(res.data))
      .catch(err => console.error(err));
  }

  useEffect(() => {
    fetchAddresses();
  }, [customerId]);

  return React.createElement(
    'div',
    null,
    React.createElement('h2', null, 'Addresses'),
    addresses.map(addr =>
      React.createElement('div', { key: addr.id },
        editingId === addr.id
          ? React.createElement(AddressForm, { address: addr, customerId, onSave: fetchAddresses })
          : React.createElement('div', null,
              React.createElement('p', null, `${addr.address}, ${addr.city}, ${addr.state} - ${addr.pincode}`),
              React.createElement('button', { onClick: () => setEditingId(addr.id) }, 'Edit')
            )
      )
    ),
    React.createElement(AddressForm, { customerId, onSave: fetchAddresses })
  );
}

export default AddressList;
