import React, { useState } from 'react';
import api from '../api/api';

const styles = {
  container: {
    maxWidth: 500,
    margin: '40px auto',
    padding: 20,
    borderRadius: 8,
    backgroundColor: '#f9f9f9',
    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  heading: {
    textAlign: 'center',
    marginBottom: 25,
    color: '#333',
  },
  label: {
    display: 'block',
    marginBottom: 6,
    fontWeight: '600',
    color: '#555',
  },
  input: {
    width: '100%',
    padding: 10,
    marginBottom: 15,
    borderRadius: 4,
    border: '1px solid #ccc',
    fontSize: 16,
  },
  button: {
    width: '100%',
    padding: 12,
    backgroundColor: '#007BFF',
    border: 'none',
    color: '#fff',
    fontSize: 16,
    fontWeight: 600,
    borderRadius: 4,
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  error: {
    color: '#d93025',
    marginBottom: 15,
    fontWeight: 600,
    textAlign: 'center',
  },
  success: {
    color: '#188038',
    marginBottom: 15,
    fontWeight: 600,
    textAlign: 'center',
  },
};

function AddCustomer() {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    phone: '',
    city: '',
    state: '',
    pincode: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [hover, setHover] = useState(false);

  function handleChange(e) {
    setFormData(function (prev) {
      return Object.assign({}, prev, { [e.target.name]: e.target.value });
    });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!formData.first_name || !formData.last_name || !formData.phone) {
      setError('First name, last name, and phone are required.');
      return;
    }

    try {
      const response = await api.post('/customers', formData);
      setSuccess('Customer added successfully! ID: ' + response.data.id);
      setFormData({
        first_name: '',
        last_name: '',
        phone: '',
        city: '',
        state: '',
        pincode: '',
      });
    } catch (err) {
      if (err.response && err.response.data && err.response.data.error) {
        setError(err.response.data.error);
      } else {
        setError('Failed to add customer. Please try again.');
      }
    }
  }

  return React.createElement(
    'div',
    { style: styles.container },
    React.createElement('h2', { style: styles.heading }, 'Add Customer'),
    error && React.createElement('p', { style: styles.error }, error),
    success && React.createElement('p', { style: styles.success }, success),

    React.createElement(
      'form',
      { onSubmit: handleSubmit },

      React.createElement('label', { style: styles.label }, 'First Name*'),
      React.createElement('input', {
        style: styles.input,
        type: 'text',
        name: 'first_name',
        value: formData.first_name,
        onChange: handleChange,
        placeholder: 'Enter first name',
        required: true,
      }),

      React.createElement('label', { style: styles.label }, 'Last Name*'),
      React.createElement('input', {
        style: styles.input,
        type: 'text',
        name: 'last_name',
        value: formData.last_name,
        onChange: handleChange,
        placeholder: 'Enter last name',
        required: true,
      }),

      React.createElement('label', { style: styles.label }, 'Phone*'),
      React.createElement('input', {
        style: styles.input,
        type: 'tel',
        name: 'phone',
        value: formData.phone,
        onChange: handleChange,
        placeholder: 'Enter phone number',
        required: true,
      }),

      React.createElement('label', { style: styles.label }, 'City'),
      React.createElement('input', {
        style: styles.input,
        type: 'text',
        name: 'city',
        value: formData.city,
        onChange: handleChange,
        placeholder: 'Enter city',
      }),

      React.createElement('label', { style: styles.label }, 'State'),
      React.createElement('input', {
        style: styles.input,
        type: 'text',
        name: 'state',
        value: formData.state,
        onChange: handleChange,
        placeholder: 'Enter state',
      }),

      React.createElement('label', { style: styles.label }, 'Pincode'),
      React.createElement('input', {
        style: styles.input,
        type: 'text',
        name: 'pincode',
        value: formData.pincode,
        onChange: handleChange,
        placeholder: 'Enter pincode',
      }),

      React.createElement(
        'button',
        {
          type: 'submit',
          style: Object.assign(
            {},
            styles.button,
            hover ? { backgroundColor: '#0056b3' } : {}
          ),
          onMouseEnter: function () {
            setHover(true);
          },
          onMouseLeave: function () {
            setHover(false);
          },
        },
        'Add Customer'
      )
    )
  );
}

export default AddCustomer;
