import React, { useState } from 'react';
import axios from '../api/api'; // Adjust the path if needed

function AddCustomer() {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    phone: '',
    city: '',
    state: '',
    pincode: ''
  });

  const handleChange = function(e) {
    const name = e.target.name;
    const value = e.target.value;
    setFormData(function(prev) {
      const updated = Object.assign({}, prev);
      updated[name] = value;
      return updated;
    });
  };

  const handleSubmit = async function(e) {
    e.preventDefault();
    try {
      const response = await axios.post('/customers', formData);
      alert('Customer added! ID: ' + response.data.id);
      setFormData({
        first_name: '',
        last_name: '',
        phone: '',
        city: '',
        state: '',
        pincode: ''
      });
    } catch (error) {
      alert('Error adding customer: ' + (error.response && error.response.data && error.response.data.error ? error.response.data.error : error.message));
    }
  };

  return React.createElement(
    'form',
    { onSubmit: handleSubmit },
    React.createElement('input', {
      type: 'text',
      name: 'first_name',
      placeholder: 'First Name',
      value: formData.first_name,
      onChange: handleChange,
      required: true
    }),
    React.createElement('input', {
      type: 'text',
      name: 'last_name',
      placeholder: 'Last Name',
      value: formData.last_name,
      onChange: handleChange,
      required: true
    }),
    React.createElement('input', {
      type: 'text',
      name: 'phone',
      placeholder: 'Phone',
      value: formData.phone,
      onChange: handleChange,
      required: true
    }),
    React.createElement('input', {
      type: 'text',
      name: 'city',
      placeholder: 'City',
      value: formData.city,
      onChange: handleChange
    }),
    React.createElement('input', {
      type: 'text',
      name: 'state',
      placeholder: 'State',
      value: formData.state,
      onChange: handleChange
    }),
    React.createElement('input', {
      type: 'text',
      name: 'pincode',
      placeholder: 'Pincode',
      value: formData.pincode,
      onChange: handleChange
    }),
    React.createElement('button', { type: 'submit' }, 'Add Customer')
  );
}

export default AddCustomer;
