import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddressForm from './AddressForm.js';

function AddressList(props) {
  const customerId = props.customerId;
  const [addresses, setAddresses] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(function () {
    axios.get('/api/addresses?customerId=' + customerId)
      .then(function (res) {
        setAddresses(res.data);
      })
      .catch(function (err) {
        console.error(err);
      });
  }, [customerId]);

  function handleEdit(id) {
    setEditingId(id);
  }

  function handleUpdate() {
    setEditingId(null);
    axios.get('/api/addresses?customerId=' + customerId)
      .then(function (res) {
        setAddresses(res.data);
      });
  }

  return React.createElement(
    'div',
    null,
    React.createElement('h2', null, 'Addresses'),
    addresses.map(function (address) {
      return React.createElement(
        'div',
        { key: address.id },
        editingId === address.id
          ? React.createElement(AddressForm, { address: address, onSave: handleUpdate })
          : React.createElement(
            'div',
            null,
            React.createElement(
              'p',
              null,
              address.address + ', ' + address.city + ', ' + address.state + ', ' + address.pincode
            ),
            React.createElement(
              'button',
              { onClick: function () { handleEdit(address.id); } },
              'Edit'
            )
          )
      );
    }),
    React.createElement(AddressForm, { customerId: customerId, onSave: handleUpdate })
  );
}

export default AddressList;
